		#region Private Methods
		private void format_controls(){
            CControlFormat.setFormStyle(this, new CAppContext_201(), IPFormStyle.DialogForm);
            CControlFormat.setC1FlexFormat(m_fg);
            CGridUtils.AddSearch_Handlers(m_fg);
            CGridUtils.AddSave_Excel_Handlers(m_fg);

            m_lbl_header.Font = new Font("Arial", 16);
            m_lbl_header.ForeColor = Color.DarkRed;
            m_lbl_header.TextAlign = ContentAlignment.MiddleCenter;

            m_fg.Tree.Style = TreeStyleFlags.SimpleLeaf;
            m_fg.Tree.Column = (int)e_col_Number.MA_SINH_VIEN;
            m_fg.Cols[(int)e_col_Number.MA_LOP].Visible = false;
            m_fg.Cols[(int)e_col_Number.MA_TRUONG].Visible = false;
            this.MaximizeBox = true;
            this.MinimizeBox = true;

            m_cmd_select.Visible = false;
            

			set_define_events();
			this.KeyPreview = true;		
		}
		private void set_initial_form_load(){
			m_obj_trans = get_trans_object(m_fg);
            m_lb_thong_bao_ket_qua.Visible = false;
            if(m_e_form_load == e_form_load.SEARCH_ON_LOAD_YES)
            load_data_2_grid();		
		}	
		private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg){
			Hashtable v_htb = new Hashtable();
            v_htb.Add(CSinhVien.HO_TEN_SINH_VIEN, e_col_Number.HO_TEN);
            v_htb.Add(CSinhVien.DIEN_THOAI_DI_DONG, e_col_Number.SO_DIEN_THOAI);
            v_htb.Add(CSinhVien.MA_LOP, e_col_Number.MA_LOP);
            v_htb.Add(CSinhVien.MA_HOC_VIEN, e_col_Number.MA_SINH_VIEN);
            v_htb.Add(CSinhVien.MA_TRUONG, e_col_Number.MA_TRUONG);
            v_htb.Add(CSinhVien.NGAY_THANG_NAM_SINH, e_col_Number.NGAY_SINH);
            v_htb.Add(CSinhVien.NOI_SINH, e_col_Number.NOI_SINH);
            DS_DM_HOC_VIEN v_ds_hoc_vien = new DS_DM_HOC_VIEN();
            v_ds_hoc_vien.EnforceConstraints = false;
            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, v_ds_hoc_vien.DM_HOC_VIEN.NewRow());
			return v_obj_trans;			
		}
        private void load_data_2_cbo_truong()
        {
            US_CM_DM_TU_DIEN v_us_tu_dien_truong = new US_CM_DM_TU_DIEN();
            DS_CM_DM_TU_DIEN v_ds_tu_dien_truong = new DS_CM_DM_TU_DIEN();
            v_us_tu_dien_truong.fill_tu_dien_cung_loai_ds(LOAI_TU_DIEN_TEXT.TO_CHUC_TRUONG, CM_DM_TU_DIEN.MA_TU_DIEN, v_ds_tu_dien_truong);
            m_cbo_truong.DataSource = v_ds_tu_dien_truong.CM_DM_TU_DIEN;
            m_cbo_truong.DisplayMember = CM_DM_TU_DIEN.TEN;
            m_cbo_truong.ValueMember = CM_DM_TU_DIEN.TEN_NGAN;
        }
        private void load_data_2_grid(){
            DataSet v_ds_hoc_vien = new DataSet();
            TOSApp.SCMServices.SyncData v_ws = new TOSApp.SCMServices.SyncData();
            string v_str_key_search = HelpUtils.gen_string_call_scm_get_sinh_vien_ws("", m_txt_key_search.Text.Trim(), "", "", "20",m_cbo_truong.Text);
            v_ds_hoc_vien = v_ws.TOS_search_info_hoc_vien(HelpUtils.gen_string_call_scm_get_sinh_vien_ws("", m_txt_key_search.Text.Trim(), "", "", "20",m_cbo_truong.Text));
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(v_ds_hoc_vien, m_fg, m_obj_trans);
            m_fg.Sort(SortFlags.Ascending, (int)e_col_Number.MA_TRUONG, (int)e_col_Number.MA_LOP);
            set_tree_on_grid();
            m_fg.Redraw = true;
            make_stt();
            m_fg.Focus();
            if(m_fg.Rows.Count > 3)
            m_fg.Row = 3;
            m_fg.Col = 3;
            if (m_fg.Rows.Count == 1)
            {
                m_lb_thong_bao_ket_qua.Visible = true;
                SizeConverter v_size = new SizeConverter();

                m_lb_thong_bao_ket_qua.Font = new System.Drawing.Font(new FontFamily("Microsoft Sans Serif"), 12);
                m_lb_thong_bao_ket_qua.ForeColor = Color.FromArgb(129,12,21);
            }
            else m_lb_thong_bao_ket_qua.Visible = false;
		}
        private void make_stt()
        {
            int v_dc_stt = 0;
            for (int i_stt = 1; i_stt <= m_fg.Rows.Count - 1; i_stt++)
            {
                if (m_fg.Rows[i_stt].IsNode)
                {
                    v_dc_stt = 0;
                }
                else
                {
                    v_dc_stt = v_dc_stt + 1;
                    m_fg[i_stt, (int)e_col_Number.STT] = v_dc_stt;
                }
            }
        }

        private void set_tree_on_grid()
        {
            m_fg.Subtotal(AggregateEnum.None
                         , 0
                         , (int)e_col_Number.MA_TRUONG
                         , (int)e_col_Number.MA_TRUONG
                         , "Mã trường: {0}");
            m_fg.Subtotal(AggregateEnum.None
                       , 1
                       , (int)e_col_Number.MA_LOP
                       , (int)e_col_Number.MA_LOP
                       , "Mã lớp: {0}");

        }
        
        private void grid2us_object(US_V_DM_CAU_HOI i_us
			, int i_grid_row) {
                m_us_cau_hoi_yeu_cau.datNGAY_SINH = CIPConvert.ToDatetime(CIPConvert.ToStr(m_fg[i_grid_row, (int)e_col_Number.NGAY_SINH]), "dd/MM/yyyy");
                m_us_cau_hoi_yeu_cau.strHO_TEN_SINH_VIEN = CIPConvert.ToStr(m_fg[i_grid_row, (int)e_col_Number.HO_TEN]);
                m_us_cau_hoi_yeu_cau.strLOP = CIPConvert.ToStr(m_fg[i_grid_row, (int)e_col_Number.MA_LOP]);
                m_us_cau_hoi_yeu_cau.strMA_SINH_VIEN = CIPConvert.ToStr(m_fg[i_grid_row, (int)e_col_Number.MA_SINH_VIEN]);
                m_us_cau_hoi_yeu_cau.strNOI_SINH = CIPConvert.ToStr(m_fg[i_grid_row, (int)e_col_Number.NOI_SINH]);
                m_us_cau_hoi_yeu_cau.strTRUONG = CIPConvert.ToStr(m_fg[i_grid_row, (int)e_col_Number.MA_TRUONG]);
		}


		private void us_object2grid(US_V_DM_CAU_HOI_KO_TRA_LOI i_us
			, int i_grid_row) {
			DataRow v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			i_us.Me2DataRow(v_dr);
			m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
		}


				



		private void set_define_events(){
			m_cmd_exit.Click += new EventHandler(m_cmd_exit_Click);
            this.Load += new EventHandler(f220_dm_cau_hoi_searching_Load);
            m_cmd_select.Click += new EventHandler(m_cmd_select_Click);
            m_txt_key_search.KeyDown += new KeyEventHandler(m_txt_key_cau_hoi_KeyDown);
            m_fg.DoubleClick += new EventHandler(m_fg_DoubleClick);
            m_fg.KeyDown += new KeyEventHandler(m_fg_KeyDown);
            m_cbo_truong.SelectedIndexChanged += new EventHandler(m_cbo_truong_SelectedIndexChanged);
		}

        void m_cbo_truong_SelectedIndexChanged(object sender, EventArgs e)
        {
            try
            {
                if (m_txt_key_search.Text.Trim() != "")
                    load_data_2_grid();
            }
            catch (Exception v_e)
            {
                CSystemLog_100.ExceptionHandle(v_e);
            }
        }

        void m_fg_KeyDown(object sender, KeyEventArgs e)
        {
            try
            {
                switch (e.KeyCode)
                {
                    case Keys.Enter:
                        if (!HelpUtils.is_validated_edit_grid_row(m_fg.Row, m_fg)) return;
                        grid2us_object(m_us_cau_hoi, m_fg.Row);
                        m_dlg_result = DialogResult.OK;
                        this.Close();
                        break;
                    default:
                        break;
                }
                
            }
            catch (Exception v_e)
            {
                CSystemLog_100.ExceptionHandle(v_e);
            }
        }

        void m_fg_DoubleClick(object sender, EventArgs e)
        {
            try
            {
                if(!HelpUtils.is_validated_edit_grid_row(m_fg.Row,m_fg)) return;
                grid2us_object(m_us_cau_hoi,m_fg.Row);
                m_dlg_result = DialogResult.OK;
                this.Close();
            }
            catch (Exception v_e)
            {
                CSystemLog_100.ExceptionHandle(v_e);
            }
        }

        void m_cmd_select_Click(object sender, EventArgs e)
        {
            try
            {
                if(!HelpUtils.is_validated_edit_grid_row(m_fg.Row,m_fg)) return;
                m_dlg_result = DialogResult.OK;
                grid2us_object(m_us_cau_hoi, m_fg.Row);
                this.Close();
            }
            catch (Exception v_e)
            {
                CSystemLog_100.ExceptionHandle(v_e);
            }
        }

        void m_txt_key_cau_hoi_KeyDown(object sender, KeyEventArgs e)
        {
            try
            {
                switch (e.KeyCode)
                {
                    case Keys.Enter:
                        load_data_2_grid();
                        break;
                    default:
                        break;
                }
            }
            catch (Exception v_e)
            {
                CSystemLog_100.ExceptionHandle(v_e);
            }
        }

        void f220_dm_cau_hoi_searching_Load(object sender, EventArgs e)
        {
            try
            {
                set_initial_form_load();
            }
            catch (Exception v_e)
            {
                CSystemLog_100.ExceptionHandle(v_e);
            }
        }
      

