            m_lbl_header.ForeColor = Color.DarkRed;
            m_lbl_header.TextAlign = ContentAlignment.MiddleCenter;

            m_fg.Tree.Style = TreeStyleFlags.SimpleLeaf;
            m_fg.Tree.Column = (int)e_col_Number.NOI_DUNG_CAU_HOI;

            m_fg.Cols[(int)e_col_Number.TEN_TO_CHUC].Visible = false;
            m_fg.Cols[(int)e_col_Number.TEN_NHOM_CAU_HOI].Visible = false;

            this.MaximizeBox = true;
            this.MinimizeBox = true;

            m_cmd_view.Visible = false;
            m_cmd_delete.Visible = false;
            m_cbo_don_vi.Focus();

			set_define_events();
			this.KeyPreview = true;
		}
		private void set_initial_form_load(){
			m_obj_trans = get_trans_object(m_fg);
            m_obj_trans_cau_tra_loi = get_trans_object_cau_tra_loi(m_fg_cau_tra_loi);

            load_data_2_cbo_don_vi_truong();
            load_data_2_cbo_nhom_cau_hoi();
            load_data_2_grid();		
		}	
		private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg){
			Hashtable v_htb = new Hashtable();
			v_htb.Add(V_DM_CAU_HOI_KO_TRA_LOI.TEN_NHOM_CAU_HOI, e_col_Number.TEN_NHOM_CAU_HOI);			v_htb.Add(V_DM_CAU_HOI_KO_TRA_LOI.NOI_DUNG_CAU_HOI, e_col_Number.NOI_DUNG_CAU_HOI);			v_htb.Add(V_DM_CAU_HOI_KO_TRA_LOI.TEN_NGUOI_CAP_NHAT_CUOI, e_col_Number.TEN_NGUOI_CAP_NHAT_CUOI);			v_htb.Add(V_DM_CAU_HOI_KO_TRA_LOI.NGAY_CAP_NHAP_CUOI, e_col_Number.NGAY_CAP_NHAP_CUOI);			v_htb.Add(V_DM_CAU_HOI_KO_TRA_LOI.TEN_TO_CHUC, e_col_Number.TEN_TO_CHUC);			v_htb.Add(V_DM_CAU_HOI_KO_TRA_LOI.TEN_NGUOI_TAO, e_col_Number.TEN_NGUOI_TAO);			v_htb.Add(V_DM_CAU_HOI_KO_TRA_LOI.NGAY_TAO, e_col_Number.NGAY_TAO);									
			ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg,v_htb,m_ds.V_DM_CAU_HOI_KO_TRA_LOI.NewRow());
			return v_obj_trans;			
		}
        private ITransferDataRow get_trans_object_cau_tra_loi(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(V_DM_CAU_TRA_LOI.NGAY_TAO, e_col_Number_cau_tra_loi.NGAY_TAO);
            v_htb.Add(V_DM_CAU_TRA_LOI.ID_CAU_HOI, e_col_Number_cau_tra_loi.ID_CAU_HOI);
            v_htb.Add(V_DM_CAU_TRA_LOI.TEN_NGUOI_TAO, e_col_Number_cau_tra_loi.NGUOI_TAO);
            v_htb.Add(V_DM_CAU_TRA_LOI.CAU_TRA_LOI, e_col_Number_cau_tra_loi.CAU_TRA_LOI);
            v_htb.Add(V_DM_CAU_TRA_LOI.TRANG_THAI, e_col_Number_cau_tra_loi.TRANG_THAI);
            v_htb.Add(V_DM_CAU_TRA_LOI.LINK_TL_THAM_KHAO, e_col_Number_cau_tra_loi.LINK_TL_THAM_KHAO);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds_cau_tra_loi.V_DM_CAU_TRA_LOI.NewRow());
            return v_obj_trans;
        }
        private void load_data_2_cbo_don_vi_truong()
        {
            m_e_trang_thai_load_to_chuc = e_trang_thai_load_cbo_to_chuc.DANG_FILL_DU_LIEU;
            US_CM_DM_TU_DIEN v_us_tu_dien_trang_thai = new US_CM_DM_TU_DIEN();
            DS_CM_DM_TU_DIEN v_ds_tu_dien_trang_thai = new DS_CM_DM_TU_DIEN();
            v_us_tu_dien_trang_thai.fill_tu_dien_cung_loai_ds(LOAI_TU_DIEN_TEXT.TO_CHUC_TRUONG, CM_DM_TU_DIEN.MA_TU_DIEN, v_ds_tu_dien_trang_thai);

            DataRow v_dr = v_ds_tu_dien_trang_thai.CM_DM_TU_DIEN.NewCM_DM_TU_DIENRow();
            v_dr[CM_DM_TU_DIEN.ID] = "0";
            v_dr[CM_DM_TU_DIEN.TEN] = "--Tất cả đơn vị--";
            v_ds_tu_dien_trang_thai.EnforceConstraints = false;
            v_ds_tu_dien_trang_thai.CM_DM_TU_DIEN.Rows.InsertAt(v_dr, 0);

            m_cbo_don_vi.DataSource = v_ds_tu_dien_trang_thai.CM_DM_TU_DIEN;
            m_cbo_don_vi.DisplayMember = CM_DM_TU_DIEN.TEN;
            m_cbo_don_vi.ValueMember = CM_DM_TU_DIEN.ID;

            m_e_trang_thai_load_to_chuc = e_trang_thai_load_cbo_to_chuc.DA_FILL_XONG_DU_LIEU;
        }
        private void load_data_2_cbo_nhom_cau_hoi()
        {
            m_e_trang_thai_load_nhom_cau_hoi = e_trang_thai_load_cbo_nhom_cau_hoi.DANG_FILL_DU_LIEU;
            US_CM_DM_TU_DIEN v_us_tu_dien_trang_thai = new US_CM_DM_TU_DIEN();
            DS_CM_DM_TU_DIEN v_ds_tu_dien_trang_thai = new DS_CM_DM_TU_DIEN();
            v_us_tu_dien_trang_thai.fill_tu_dien_cung_loai_ds(LOAI_TU_DIEN_TEXT.NHOM_CAU_HOI, CM_DM_TU_DIEN.MA_TU_DIEN, v_ds_tu_dien_trang_thai);

            DataRow v_dr = v_ds_tu_dien_trang_thai.CM_DM_TU_DIEN.NewCM_DM_TU_DIENRow();
            v_dr[CM_DM_TU_DIEN.ID] = "0";
            v_dr[CM_DM_TU_DIEN.TEN] = "--Tất cả nhóm--";
            v_ds_tu_dien_trang_thai.EnforceConstraints = false;
            v_ds_tu_dien_trang_thai.CM_DM_TU_DIEN.Rows.InsertAt(v_dr, 0);

            m_cbo_nhom_cau_hoi.DataSource = v_ds_tu_dien_trang_thai.CM_DM_TU_DIEN;
            m_cbo_nhom_cau_hoi.DisplayMember = CM_DM_TU_DIEN.TEN;
            m_cbo_nhom_cau_hoi.ValueMember = CM_DM_TU_DIEN.ID;
            m_e_trang_thai_load_nhom_cau_hoi = e_trang_thai_load_cbo_nhom_cau_hoi.DA_FILL_XONG_DU_LIEU;
        }
        
        private void load_data_2_grid(){						
			m_ds = new DS_V_DM_CAU_HOI_KO_TRA_LOI();
            m_us.load_danh_sach_cau_hoi(m_ds
                             , CIPConvert.ToDecimal(m_cbo_don_vi.SelectedValue)
                            , CIPConvert.ToDecimal(m_cbo_nhom_cau_hoi.SelectedValue));
			m_fg.Redraw = false;
			CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            set_tree_on_grid();
            m_fg.Redraw = true;
            CGridUtils.stand_on_TopLeft_Cell(m_fg);
            load_data_2_grid_cau_tra_loi(0);
		}
        private void load_data_2_grid_cau_tra_loi(decimal ip_dc_id_cau_hoi)
        {
            m_ds_cau_tra_loi = new DS_V_DM_CAU_TRA_LOI();
            m_us_cau_tra_loi.load_danh_sach_cau_tra_loi(m_ds_cau_tra_loi, ip_dc_id_cau_hoi); 
            m_fg_cau_tra_loi.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds_cau_tra_loi, m_fg_cau_tra_loi, m_obj_trans_cau_tra_loi);
            m_fg_cau_tra_loi.Redraw = true;
        }
        private void load_ds_cau_tra_loi(int ip_i_cau_hoi_row)
        {
            if (!HelpUtils.is_validated_edit_grid_row(ip_i_cau_hoi_row, m_fg)) return;
            US_V_DM_CAU_HOI_KO_TRA_LOI v_us_dm = new US_V_DM_CAU_HOI_KO_TRA_LOI();
            grid2us_object(v_us_dm, m_fg.Row);
            load_data_2_grid_cau_tra_loi(v_us_dm.dcID);
        }

        private void set_tree_on_grid()
        {
            m_fg.Subtotal(AggregateEnum.None
                         , 0
                         , (int)e_col_Number.TEN_TO_CHUC
                         , (int)e_col_Number.TEN_NHOM_CAU_HOI
                         , "Tổ chức, đơn vị: {0}");
            m_fg.Subtotal(AggregateEnum.None
                       , 1
                       , (int)e_col_Number.TEN_NHOM_CAU_HOI
                       , (int)e_col_Number.TEN_NHOM_CAU_HOI
                       , "Nhóm câu hỏi: {0}");

        }
        
        private void grid2us_object(US_V_DM_CAU_HOI_KO_TRA_LOI i_us
			, int i_grid_row) {
			DataRow v_dr;
			v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			m_obj_trans.GridRow2DataRow(i_grid_row,v_dr);
			i_us.DataRow2Me(v_dr);
		}

        private void grid2us_object_cau_tra_loi(US_V_DM_CAU_TRA_LOI i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg_cau_tra_loi.Rows[i_grid_row].UserData;
            m_obj_trans_cau_tra_loi.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }

		private void us_object2grid(US_V_DM_CAU_HOI_KO_TRA_LOI i_us
			, int i_grid_row) {
			DataRow v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			i_us.Me2DataRow(v_dr);
			m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
		}


		private void insert_v_dm_cau_hoi_ko_tra_loi(){
            f111_dm_cau_hoi_de v_f111 = new f111_dm_cau_hoi_de();
            v_f111.display_for_insert_cau_hoi();
			load_data_2_grid();
		}

		private void update_v_dm_cau_hoi_ko_tra_loi(){
            if (!HelpUtils.is_validated_edit_grid_row(m_fg.Row, m_fg)) return;	
			grid2us_object(m_us, m_fg.Row);
            f111_dm_cau_hoi_de v_f111 = new f111_dm_cau_hoi_de();
            m_us_cau_hoi = new US_V_DM_CAU_HOI(m_us.dcID);
            v_f111.display_for_update_cau_hoi(m_us_cau_hoi);
			load_data_2_grid();
		}
				
		private void delete_v_dm_cau_hoi_ko_tra_loi(){
            if (!HelpUtils.is_validated_edit_grid_row(m_fg.Row, m_fg)) return;
			if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted)  return;
			US_V_DM_CAU_HOI_KO_TRA_LOI v_us = new US_V_DM_CAU_HOI_KO_TRA_LOI();
			grid2us_object(v_us, m_fg.Row);
			try {			
				v_us.BeginTransaction();    											
				v_us.Delete();                      								
				v_us.CommitTransaction();
				m_fg.Rows.Remove(m_fg.Row);				
			}
			catch (Exception v_e) {
				v_us.Rollback();
				CDBExceptionHandler v_objErrHandler = new CDBExceptionHandler(v_e,
					new CDBClientDBExceptionInterpret());
				v_objErrHandler.showErrorMessage();
			}
		}

		private void view_v_dm_cau_hoi_ko_tra_loi(){			
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
			grid2us_object(m_us, m_fg.Row);
		//	f110_dm_cau_hoi_DE v_fDE = new f110_dm_cau_hoi_DE();			
		//	v_fDE.display(m_us);
		}

        private void duyet_cau_tra_loi()
        {
            if (!HelpUtils.is_validated_edit_grid_row(m_fg_cau_tra_loi.Row, m_fg_cau_tra_loi)) return;
            grid2us_object_cau_tra_loi(m_us_cau_tra_loi, m_fg_cau_tra_loi.Row);
            f111_dm_cau_hoi_de v_f111 = new f111_dm_cau_hoi_de();
            v_f111.display_for_duyet_cau_tra_loi(m_us_cau_tra_loi);
            load_data_2_grid_cau_tra_loi(m_us_cau_tra_loi.dcID_CAU_HOI);
        }
        private void them_cau_tra_loi()
        {
            if (!HelpUtils.is_validated_edit_grid_row(m_fg.Row, m_fg)) return;
            grid2us_object(m_us, m_fg.Row);
            f111_dm_cau_hoi_de v_f111 = new f111_dm_cau_hoi_de();
            m_us_cau_hoi = new US_V_DM_CAU_HOI(m_us.dcID);
            v_f111.display_for_them_cau_tra_loi(m_us_cau_hoi);
            load_data_2_grid_cau_tra_loi(m_us_cau_hoi.dcID);
        }

		private void set_define_events(){
			m_cmd_exit.Click += new EventHandler(m_cmd_exit_Click);
			m_cmd_insert.Click += new EventHandler(m_cmd_insert_Click);
			m_cmd_update.Click += new EventHandler(m_cmd_update_Click);
			m_cmd_delete.Click += new EventHandler(m_cmd_delete_Click);
			m_cmd_view.Click += new EventHandler(m_cmd_view_Click);

            m_cmd_them_cau_tra_loi.Click += new EventHandler(m_cmd_them_cau_tra_loi_Click);
            m_cmd_duyet_cau_tra_loi.Click += new EventHandler(m_cmd_duyet_cau_tra_loi_Click);

            m_cbo_don_vi.SelectedIndexChanged += new EventHandler(m_cbo_don_vi_SelectedIndexChanged);
            m_cbo_nhom_cau_hoi.SelectedIndexChanged += new EventHandler(m_cbo_nhom_cau_hoi_SelectedIndexChanged);
            m_fg.Click += new EventHandler(m_fg_Click);
            this.KeyDown += new KeyEventHandler(f110_dm_cau_hoi_KeyDown);
		}

        void f110_dm_cau_hoi_KeyDown(object sender, KeyEventArgs e)
        {
            try
            {
                switch (e.KeyCode)
                {
                    case Keys.Escape:
                        this.Close();
                        break;
                }
            }
            catch (Exception v_e)
            {
                CSystemLog_100.ExceptionHandle(v_e);
            }
        }
      

